import React, { useState } from "react";
import "../styles/assessment.css";

const PHQ9Form = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    scores: Array(9).fill(0),
  });
  const [result, setResult] = useState(null);
  const [formSubmitted, setFormSubmitted] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === "name" || name === "email") {
      setFormData({ ...formData, [name]: value });
    } else {
      const index = parseInt(name.replace("q", ""), 10) - 1;
      const updatedScores = [...formData.scores];
      updatedScores[index] = parseInt(value, 10);
      setFormData({ ...formData, scores: updatedScores });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, scores } = formData;

    if (!name || !email) {
      alert("Name and email are required to submit the test.");
      return;
    }

    const totalScore = scores.reduce((sum, score) => sum + score, 0);
    let severity = "";
    let message = "";
    let emoji = "";

    if (totalScore === 0) {
      severity = "No depression";
      message = `Great, ${name}! You have no signs of depression. Keep maintaining your mental well-being!`;
      emoji = "ðŸ˜Š";
    } else if (totalScore >= 1 && totalScore <= 4) {
      severity = "Minimal depression";
      message = `Hi ${name}, this might be due to stress. No need to worryâ€”you can take steps to manage it yourself.`;
      emoji = "ðŸŒŸ";
    } else if (totalScore >= 5 && totalScore <= 9) {
      severity = "Mild depression";
      message = `${name}, consider stress-relieving activities or talking to a trusted person.`;
      emoji = "ðŸ’¡";
    } else if (totalScore >= 10 && totalScore <= 14) {
      severity = "Moderate depression";
      message = `It may be helpful to consult a mental health professional for guidance, ${name}.`;
      emoji = "ðŸ§ ";
    } else if (totalScore >= 15 && totalScore <= 19) {
      severity = "Moderately severe depression";
      message = `${name}, meeting with a psychiatrist or therapist is strongly recommended.`;
      emoji = "ðŸ©º";
    } else if (totalScore >= 20) {
      severity = "Severe depression";
      message = `${name}, please seek immediate help from a mental health professional.`;
      emoji = "ðŸš¨";
    }

    setResult({
      totalScore,
      severity,
      message: `${emoji} ${message}`,
    });
    setFormSubmitted(true);

    const testData = {
      name,
      email,
      score: totalScore,
      severity,
    };

    try {
      const response = await fetch("http://localhost:5001/api/saveTestResult", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(testData),
      });

      if (response.ok) {
        console.log("Test results saved successfully.");
      } else {
        console.error("Failed to save test results.");
      }
    } catch (error) {
      console.error("Error saving test results:", error);
    }
  };

  return (
    <div>
      <header>
        <h1>Self Assessment Test</h1>
      </header>

      <div className="container">
        {!formSubmitted ? (
          <form onSubmit={handleSubmit}>
            <div className="user-info">
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
            </div>

            <p>
              Over the last 2 weeks, how often have you been bothered by any of
              the following problems?
            </p>

            {[...Array(9)].map((_, index) => (
              <div className="question" key={index}>
                <p>{`${index + 1}. Question text here`}</p>
                {[...Array(4)].map((_, value) => (
                  <label key={value}>
                    <input
                      type="radio"
                      name={`q${index + 1}`}
                      value={value}
                      onChange={handleInputChange}
                      required
                    />
                    {["Not at all", "Several days", "More than half the days", "Nearly every day"][value]}
                  </label>
                ))}
              </div>
            ))}

            <button type="submit" className="submit-btn">
              Submit
            </button>
          </form>
        ) : (
          <div id="result">
            <h2>Your Results</h2>
            <p id="score">Your total score is: {result.totalScore}</p>
            <p id="severity">Depression Severity: {result.severity}</p>
            <p id="message">{result.message}</p>
          </div>
        )}
      </div>

      <footer>
        <p>&copy; 2024 PHQ-9 Assessment Tool</p>
      </footer>
    </div>
  );
};

export default PHQ9Form;
