import React, { useState } from "react";
import "./ques.css";

const Ques = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    answers: {},
  });
  const [result, setResult] = useState(null);

  const questions = [
    "Little interest or pleasure in doing things",
    "Feeling down, depressed, or hopeless",
    "Trouble falling or staying asleep, or sleeping too much",
    "Feeling tired or having little energy",
    "Poor appetite or overeating",
    "Feeling bad about yourself or that you are a failure or have let yourself or your family down",
    "Trouble concentrating on things, such as reading the newspaper or watching television",
    "Moving or speaking so slowly that other people could have noticed. Or the opposite: being so fidgety or restless that you have been moving around a lot more than usual",
    "Thoughts that you would be better off dead, or of hurting yourself",
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === "name" || name === "email") {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        answers: {
          ...prevData.answers,
          [name]: parseInt(value, 10),
        },
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const { name, email, answers } = formData;
    if (!name || !email) {
      alert("Name and email are required to submit the test.");
      return;
    }

    const totalScore = Object.values(answers).reduce((sum, val) => sum + val, 0);

    let severity = "";
    let message = "";
    let emoji = "";

    if (totalScore === 0) {
      severity = "No depression";
      message = `Great, ${name}! You have no signs of depression. Keep maintaining your mental well-being!`;
      emoji = "ðŸ˜Š";
    } else if (totalScore <= 4) {
      severity = "Minimal depression";
      message = `Hi ${name}, this might be due to stress. No need to worryâ€”you can take steps to manage it yourself.`;
      emoji = "ðŸŒŸ";
    } else if (totalScore <= 9) {
      severity = "Mild depression";
      message = `${name}, consider stress-relieving activities or talking to a trusted person.`;
      emoji = "ðŸ’¡";
    } else if (totalScore <= 14) {
      severity = "Moderate depression";
      message = `It may be helpful to consult a mental health professional for guidance, ${name}.`;
      emoji = "ðŸ§ ";
    } else if (totalScore <= 19) {
      severity = "Moderately severe depression";
      message = `${name}, meeting with a psychiatrist or therapist is strongly recommended.`;
      emoji = "ðŸ©º";
    } else {
      severity = "Severe depression";
      message = `${name}, please seek immediate help from a mental health professional.`;
      emoji = "ðŸš¨";
    }

    setResult({ totalScore, severity, message: `${emoji} ${message}` });

    const testData = { name, email, score: totalScore, severity };

    fetch("http://localhost:5001/api/saveTestResult", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(testData),
    })
      .then((response) => {
        if (response.ok) {
          console.log("Test results saved successfully.");
        } else {
          console.error("Failed to save test results.");
        }
      })
      .catch((error) => console.error("Error saving test results:", error));
  };

  return (
    <div className="container">
      <header>
        <h1>Self Assessment Test</h1>
      </header>
      {!result ? (
        <form onSubmit={handleSubmit}>
          <div className="user-info">
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>

          <p>
            Over the last 2 weeks, how often have you been bothered by any of
            the following problems?
          </p>

          {questions.map((question, index) => (
            <div className="question" key={`q${index + 1}`}>
              <p>{index + 1}. {question}</p>
              <label>
                <input
                  type="radio"
                  name={`q${index + 1}`}
                  value="0"
                  onChange={handleInputChange}
                  required
                /> Not at all
              </label>
              <label>
                <input
                  type="radio"
                  name={`q${index + 1}`}
                  value="1"
                  onChange={handleInputChange}
                /> Several days
              </label>
              <label>
                <input
                  type="radio"
                  name={`q${index + 1}`}
                  value="2"
                  onChange={handleInputChange}
                /> More than half the days
              </label>
              <label>
                <input
                  type="radio"
                  name={`q${index + 1}`}
                  value="3"
                  onChange={handleInputChange}
                /> Nearly every day
              </label>
            </div>
          ))}

          <button type="submit" className="submit-btn">Submit</button>
        </form>
      ) : (
        <div id="result">
          <h2>Your Results</h2>
          <p>Your total score is: {result.totalScore}</p>
          <p>Depression Severity: {result.severity}</p>
          <p>{result.message}</p>
        </div>
      )}
      <footer>
        <p>&copy; 2024 PHQ-9 Assessment Tool</p>
      </footer>
    </div>
  );
};

export default Ques;
